mapper.insert:
INSERT INTO gar_menu_permission
(
    menu_id,
    permission_id
)
VALUES
    (
        #{menuId},
        #{permissionId}
    );

mapper.delete:
DELETE
FROM gar_menu_permission mp
WHERE mp.menu_id = #{menuId}

mapper.select:
SELECT mp.permission_id
FROM gar_menu_permission mp
WHERE mp.menu_id = #{menuId}
