mapper.insert:
INSERT INTO gar_role_authority
(
    role_id, authority_id
)
VALUES
    (
        #{roleId}, #{authorityId}
    )

mapper.delete:
DELETE FROM gar_role_authority
WHERE role_id IN

    #{id}

mapper.select:
SELECT sub.name
FROM () AS sub
WHERE sub.role_id = #{roleId}

SELECT authority_id
FROM gar_role_authority
WHERE role_id = #{roleId}
