mapper.select:
		select d.*,(select p.name from gar_sys_dept p where p.dept_id = d.parent_id) as parentName
		from gar_sys_dept d where d.del_flag = 0

			and d.dept_id in (${deptFilter})



                order by d.${sidx} ${order}


                order by d.order_num asc



			limit #{offset}, #{limit}

mapper.update:
update gar_sys_dept 

	parent_id = #{parentId}, 
	name = #{name}, 
	order_num = #{orderNum}

where dept_id = #{deptId}
