mapper.select:
select r.*, (select d.name from sys_dept d where d.dept_id = r.dept_id) deptName from gar_sys_role r


		and r.role_name like concat('%',#{roleName},'%')




		order by r.${sidx} ${order}


		order by r.role_id asc



	limit #{offset}, #{limit}

mapper.update:
update gar_sys_role
role_name = #{roleName}, remark = #{remark}, dept_id = #{deptId},
where role_id = #{roleId}

mapper.delete:
delete from gar_sys_role where role_id in 

	#{roleId}

;
delete from gar_sys_role_menu where role_id in 

	#{roleId}

;
delete from gar_sys_role_dept where role_id in

	#{roleId}

;
delete from sys_user_role where role_id in 

	#{roleId}
